import '../App.css';
import { Button, Modal, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import React from 'react';
import RunList from './RunList';
import UserProfil from './UserProfil';
import '../style.css';
import VrTrailApi from '../api/VrTrailApi';
import RunModel from '../api/runModel';


class Home extends React.Component {


  	/** Constructor of the app, which initializes firebase  */
	constructor(props) {
		super(props);

		// Init an empty state
		this.state = {
			show: false,
			showRunList: false,
			user: this.props.currentUser,
		};
	}

// API Anbindung um die Lerngruppe ihm Backend hinzuzufügen
postRun = () => {
  var today = new Date();
  console.log()
  let newRun = new RunModel();
  newRun.setID(0)
  newRun.setDate(today)
  newRun.setUserId(this.state.currentUser.id)
  VrTrailApi.getAPI().postRun(newRun)
  .then(runBO =>
    // Backend call sucessfull
    // reinit the dialogs state for a new empty customer
    this.setState({
      newRun: runBO
    })).catch(e =>
    this.setState({
      updatingInProgress: false,    // disable loading indicator
      updatingError: e              // show error message
    })
  );

  // set loading to true
  this.setState({
    updatingInProgress: true,       // show loading indicator
    updatingError: null             // disable error message
  });
}

  render() {
    return (
        <div className="d-flex flex-column mb-3">

          <div className="p-2">
              <UserProfil selectedUser={this.props.currentUser}></UserProfil>
          </div>

          <div className="p-2">
            <Button id="run-button">Neuer Durchlauf starten</Button>
          </div>

          <div className="p-2">
              <RunList selectedUser={this.props.currentUser} runs={this.props.runs}></RunList>
          </div>

        </div>
    );
  }
}

export default Home;
