import './App.css';
import { Container, Navbar, Dropdown, Button, Nav, Modal, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import React, { useState } from 'react';
import UserDropdown from './components/UserDropdown';
import RunList from './components/RunList';
import './style.css';


function App() {

  const [show, setShow] = useState(false);
  const [showRunList, setShowRunList] = useState(false)
  const [user, setUser] = useState(false)
  this.handleTextChange = this.handleTextChange.bind(this);

  const handleClose = () => setShow(false);
  const handleShow = () => {
    setShow(true);
  }
  const handleShowRunList = () => {
    setShowRunList(true);
    console.log("showRunList", showRunList)
  }

  const handleUser = (bool) => {
    setUser(bool);
    console.log("showRunList", showRunList)
  }

  return (
    <>
      <style type="text/css">
      {`
        .game-title {
          color: orange;
          font-weight: 800;
          font-size: 40px;
        }

        .medicine-app {
          font-size: 20px;
          margin-bottom: -15px;
        }

        .content {
          margin-top: 20px;
          margin-bottom: 20px;
        }

        .button-style {
          background-color: white;
          border: 1px solid rgba(0, 0, 0, 0.55);
          color: rgba(0, 0, 0, 0.55);
        }

        .button-style:hover {
          background-color: orange;
          border: 1px solid orange;
        }

        #create-user {
          margin-left: 15px;
        }

        .submit-button {
          background-color: orange;
          border: 1px solid orange;
        }

        .submit-button:hover {
          background-color: orange;
          border: 1px solid orange;
          color: black;
        }

        .run-list-entry {
          margin-top: 20px;
        }

      `}
    </style>
    <Navbar bg="light" variant="light">
      <Container>
        <Navbar.Brand href="#home" className="game-title">Duranki</Navbar.Brand>
        <Navbar.Text className="medicine-app">- Medizinapp</Navbar.Text>
        <Navbar.Toggle />
        <Navbar.Collapse className="justify-content-end">
          <Navbar.Text>
            Made by HOLA - Vr Trail Walking Group
          </Navbar.Text>
        </Navbar.Collapse>
      </Container>
    </Navbar>
    <Container className="content">

      <Nav className="me-auto">

        <Dropdown>
          <Dropdown.Toggle variant="success" className="button-style">
            Nutzer ausw√§hlen
          </Dropdown.Toggle>
          <Dropdown.Menu>
            <UserDropdown handleUser={handleUser}></UserDropdown>
          </Dropdown.Menu>
        </Dropdown>

        <Button variant="success" className="button-style" id="create-user" onClick={handleShow}>Neuen Nutzer anlegen</Button>

        <RunList show={showRunList}></RunList>

        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Neuen Nutzer anlegen</Modal.Title>
          </Modal.Header>
          <Modal.Body>
          <Form>
            <Form.Group className="mb-3" controlId="formBasicName">
              <Form.Label>Name</Form.Label>
              <Form.Control type="name" placeholder="Enter Name" />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicAlter">
              <Form.Label>Alter</Form.Label>
              <Form.Control type="alter" placeholder="Alter" />
            </Form.Group>
            <Button variant="primary" type="submit" className="submit-button">
              Nutzer anlegen
            </Button>
          </Form>
          </Modal.Body>
        </Modal>

      </Nav>

    </Container>
  </>
  );
}

export default App;
