import './App.css';
import { Container, Navbar, Dropdown, Button, Nav, Modal, Form } from 'react-bootstrap';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.css';
import React from 'react';
import UserDropdown from './components/UserDropdown';
import RunList from './components/RunList';
import UserProfil from './components/UserProfil';
import Home from './components/Home';
import './style.css';


class App extends React.Component {

  	/** Constructor of the app, which initializes firebase  */
	constructor(props) {
		super(props);
    this.handleUserChange = this.handleUserChange.bind(this);

		// Init an empty state
		this.state = {
			show: false,
			showRunList: false,
			user: ' '
		};
	}

  handleUserChange(newUser) {
    this.setState({user: newUser});
    console.log("User_Right", newUser)
  }

  handleClose = () => {
    this.setState({
      show: false
    });
  }    
  handleShow = () => {
    this.setState({
      show: true
    });
  }

  render() {
    return (
      <>
      <Navbar bg="light" variant="light">
        <Container>
          <Navbar.Brand href="#home" className="game-title">Duranki</Navbar.Brand>
          <Navbar.Text className="medicine-app">- Medizinapp</Navbar.Text>
          <Navbar.Toggle />
          <Navbar.Collapse className="justify-content-end">
            <Navbar.Text>
              Made by HOLA - Vr Trail Walking Group
            </Navbar.Text>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <Container className="content">
      <BrowserRouter>
        <Routes>
              {
                // Is a user signed in?
                this.user?
                  <>
                    <Navigate from='/' to='home'/>

                    <Route exact path='/home' component={Home}>
                      <Home user={this.user}/>
                    </Route>

                    <Route path='/userprofile'>
                      <UserProfil user={this.user}/>
                    </Route>

                    <Route path='/runs'>
                      <RunList user={this.user}/>
                    </Route>

                  </>
                  :
                  // else show the sign in page
                  <>
                    <Navigate to='/home'/>
                  </>

              }

          </Routes>
        </BrowserRouter>

      </Container>
    </>
    );
  }
}

export default App;
