import React, { Component } from 'react';
import { Button, Accordion } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import VrTrailApi from '../api/VrTrailApi';
import RunListEntry from './RunListEntry';

class RunList extends Component {

    constructor(props) {
        super(props);

        // Init an empty state
        this.state = {
          run : [],
          open: false,
          showTest: true,
          currentUser: null,
          currentUserName: props.user,
          currentUserAge: null,
        };
    }

    handleOpen = () => {
        this.setState({
            open: true
        });
    }
    handleClose = () => {
        this.setState({
            open: false
        })
    }

    getRun = () => {
        VrTrailApi.getAPI().getRun()
        .then(runModels =>
            this.setState({
                run: runModels,
                error: null,
                loadingInProgress: false,
            }))
            .catch(e =>
                this.setState({
                    run: [],
                    error: e,
                    loadingInProgress: false,
                }));
        this.setState({
            error: null,
            loadingInProgress: true,
            loadingRunError: null
        });
}

    getUserByName = () => {
        console.log(this.state.currentUserName)
		VrTrailApi.getAPI().getUserByName(this.state.currentUserName)
			.then(user =>
				this.setState({
                    currentUser: user,
                    currentUserAge: user.age,
					error: null,
					loadingInProgress: false,
				})).catch(e =>
					this.setState({
                        currentUser: null,
                        currentUserAge: null,
						error: e,
						loadingInProgress: false,
					}));
			this.setState({
				error: null,
				loadingInProgress: true
			});
	}

    showRuns() {
        console.log("He es geht")
    }

    componentDidMount() {
		this.getRun()
        this.handleOpen()
    }


    render() {
        const { run, showTest }  = this.state;
        const { show } = this.props;
        return (
            showTest ?
            <div>
                { 
                run.map((item) => 
                    <RunListEntry key={run.getID()} run={item} expandedState={run.getID()}
                    onExpandedStateChange={this.onExpandedStateChange}
                    />
                )
                }
            </div>
        : null
        );
    }
  }
  export default RunList;
