import '../App.css';
import { Button, Modal, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import React from 'react';
import UserDropdown from './UserDropdown';
import UserInformation from './UserInformation';
import '../style.css';
import VrTrailApi from '../api/VrTrailApi';


class Home extends React.Component {


  	/** Constructor of the app, which initializes firebase  */
	constructor(props) {
		super(props);
        this.handleUserChange = this.handleUserChange.bind(this)

		// Init an empty state
		this.state = {
			show: false,
			showRunList: false,
			user: null,
      selectedUser: null,
      currentUser: null
		};
	}

  handleUserChange(newUser) {
    this.setState({selectedUser: newUser});
    this.getUserById(newUser)
  }

  handleClose = () => {
    this.setState({
      show: false
    });
  }    

  handleShow = () => {
    this.setState({
      show: true
    });
  }

  getUserById = (selectedUser) => {
    VrTrailApi.getAPI().getUserById(selectedUser)
        .then(user =>
            this.setState({
                currentUser: user,
                currentUserAge: user.age,
                error: null,
                loadingInProgress: false,
            })).then(() => {
              this.getRunByIdUser(this.state.selectedUser)
            }).catch(e =>
                this.setState({
                    currentUser: null,
                    currentUserAge: null,
                    error: e,
                    loadingInProgress: false,
                }));
        this.setState({
            error: null,
            loadingInProgress: true
        });
  };

  getRunByIdUser = (selectedUserId) => {
    VrTrailApi.getAPI().getRunByIdUser(selectedUserId)
    .then(runModels =>
        this.setState({
            runs: runModels,
            error: null,
            loadingInProgress: false,
        }))
        .catch(e =>
            this.setState({
                runs: [],
                error: e,
                loadingInProgress: false,
            }));
    this.setState({
        error: null,
        loadingInProgress: true,
        loadingRunError: null
    });
}

  render() {
    return (
      <>
          <div className="d-flex flex-column mb-3">

            <div className="p-2">
              <div className="d-flex flex-row mb-3">
                <div className="p-2">
                  <UserDropdown handleUser={this.handleUserChange}></UserDropdown>
                </div>
                <div className="p-2">
                  <Button variant="success" className="button-style" id="create-user" onClick={this.handleShow}>Neuen Nutzer anlegen</Button>
                </div>
              </div>
            </div>
            {
            this.state.currentUser ?
            <div className="p-2">
              <UserInformation currentUser={this.state.currentUser}></UserInformation>
            </div>
            : null
            }

          </div>

          <Modal onHide={this.handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>Neuen Nutzer anlegen</Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <Form>
              <Form.Group className="mb-3" controlId="formBasicName">
                <Form.Label>Name</Form.Label>
                <Form.Control type="name" placeholder="Enter Name" />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicAlter">
                <Form.Label>Alter</Form.Label>
                <Form.Control type="alter" placeholder="Alter" />
              </Form.Group>
              <Button variant="primary" type="submit" className="submit-button">
                Nutzer anlegen
              </Button>
            </Form>
            </Modal.Body>
          </Modal>
    </>
    );
  }
}

export default Home;
