import React, { Component } from 'react';
import { Button, Accordion } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import VrTrailApi from '../api/VrTrailApi';
import RunListEntry from './RunListEntry';

class RunList extends Component {

    constructor(props) {
        super(props);

        // Init an empty state
        this.state = {
          run : [],
          open: false,
          currentUser: props.selectedUser,
          currentUserName: props.user,
          currentUserAge: null,
        };
    }

    getRunByIdUser = () => {
        VrTrailApi.getAPI().getRunByIdUser(this.props.selectedUser.id)
        .then(runModels =>
            this.setState({
                run: runModels,
                error: null,
                loadingInProgress: false,
            }))
            .catch(e =>
                this.setState({
                    run: [],
                    error: e,
                    loadingInProgress: false,
                }));
        this.setState({
            error: null,
            loadingInProgress: true,
            loadingRunError: null
        });
    }

    getUserByName = () => {
        console.log(this.props.selectedUser.name)
		VrTrailApi.getAPI().getUserByName(this.props.selectedUser.name)
			.then(user =>
				this.setState({
                    currentUser: user,
                    currentUserAge: user.age,
					error: null,
					loadingInProgress: false,
				})).catch(e =>
					this.setState({
                        currentUser: null,
                        currentUserAge: null,
						error: e,
						loadingInProgress: false,
					}));
			this.setState({
				error: null,
				loadingInProgress: true
			});
	}

    componentDidMount() {
		this.getRunByIdUser()
    }


    render() {
        return (
            <div>
                { 
                this.state.run.map((item) => 
                    <Accordion>
                        <Accordion.Item eventKey={item.id - 1}>
                            <Accordion.Header>Durchlauf {item.id}</Accordion.Header>
                                <RunListEntry key={this.state.run.id} run={item} expandedState={this.state.run.id}
                                onExpandedStateChange={this.onExpandedStateChange}
                                />
                         </Accordion.Item>
                    </Accordion>
                )
                }
            </div>
        : null
        );
    }
  }
  export default RunList;
