import '../App.css';
import { Button, Modal, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import React from 'react';
import RunList from './RunList';
import '../style.css';


class Home extends React.Component {


  	/** Constructor of the app, which initializes firebase  */
	constructor(props) {
		super(props);

		// Init an empty state
		this.state = {
			show: false,
			showRunList: false,
			user: this.props.selectedUser,
            selectedUser: null
		};
	}

  handleUserChange(newUser) {
    this.setState({selectedUser: newUser});
    console.log("User_Right", newUser)
  }

  getUser() {
    this.setState({selectedUser: this.props.selectedUser});
  }

  handleClose = () => {
    this.setState({
      show: false
    });
  }    
  handleShow = () => {
    this.setState({
      show: true
    });
  }

  componentDidMount() {
    this.getUser.bind(this)
  }

  render() {
    console.log("userinfo", this.props.selectedUser)
    console.log("userinfo", this.props)
    return (
      <>
          <div className="d-flex flex-column mb-3">

            <div className="p-2">

            </div>

            <div className="p-2">
              <RunList user={this.user}></RunList>
            </div>

          </div>
    </>
    );
  }
}

export default Home;
