import '../App.css';
import { Button, Modal, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import React from 'react';
import UserDropdown from './UserDropdown';
import RunList from './RunList';
import '../style.css';


class Home extends React.Component {


  	/** Constructor of the app, which initializes firebase  */
	constructor(props) {
		super(props);

		// Init an empty state
		this.state = {
			show: false,
			showRunList: false,
			user: null,
      selectedUser: null
		};
	}

  handleUserChange(newUser) {
    this.setState({selectedUser: newUser});
    console.log("User_Right", newUser)
  }

  handleClose = () => {
    this.setState({
      show: false
    });
  }    
  handleShow = () => {
    this.setState({
      show: true
    });
  }

  render() {
    return (
      <>
          <div className="d-flex flex-column mb-3">

            <div className="p-2">
              <div className="d-flex flex-row mb-3">
                <div className="p-2">
                  <UserDropdown selectedUser={this.selectedUser} onClick={this.handleUserChange}></UserDropdown>
                </div>
                <div className="p-2">
                  <Button variant="success" className="button-style" id="create-user" onClick={this.handleShow()}>Neuen Nutzer anlegen</Button>
                </div>
              </div>
            </div>

            <div className="p-2">
              <RunList user={this.user}></RunList>
            </div>

          </div>


    </>
    );
  }
}

export default Home;
