import './App.css';
import { Container, Navbar, Dropdown, Button, Nav, Modal, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import React, { useState } from 'react';
import UserDropdown from './components/UserDropdown';
import RunList from './components/RunList';
import './style.css';


class App extends React.Component {

  shouldComponentUpdate = () => false

  	/** Constructor of the app, which initializes firebase  */
	constructor(props) {
		super(props);
    // this.handleUserChange = this.handleUserChange.bind(this);

		// Init an empty state
		this.state = {
			show: false,
			showRunList: false,
			user: null,
      selectedUser: null
		};
	}

  handleUserChange(newUser) {
    this.setState({user: newUser});
    console.log("User_Right", newUser)
  }

  handleClose = () => {
    this.setState({
      show: false
    });
  }    
  handleShow = () => {
    this.setState({
      show: true
    });
  }

  render() {
    return (
      <>
      <Navbar bg="light" variant="light">
        <Container>
          <Navbar.Brand href="#home" className="game-title">Duranki</Navbar.Brand>
          <Navbar.Text className="medicine-app">- Medizinapp</Navbar.Text>
          <Navbar.Toggle />
          <Navbar.Collapse className="justify-content-end">
            <Navbar.Text>
              Made by HOLA - Vr Trail Walking Group
            </Navbar.Text>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <Container className="content">

        <Nav className="me-auto">
          <div className="d-flex flex-column mb-3">

            <div className="p-2">
              <div className="d-flex flex-row mb-3">
                <div className="p-2">
                  <UserDropdown selectedUser={selectedUser} handleUserChange={this.handleUserChange}></UserDropdown>
                </div>
                <div className="p-2">
                  <Button variant="success" className="button-style" id="create-user" onClick={this.handleShow()}>Neuen Nutzer anlegen</Button>
                </div>
              </div>
            </div>

            <div className="p-2">
              <RunList user={this.user}></RunList>
            </div>

          </div>

          <Modal onHide={this.handleClose()}>
            <Modal.Header closeButton>
              <Modal.Title>Neuen Nutzer anlegen</Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <Form>
              <Form.Group className="mb-3" controlId="formBasicName">
                <Form.Label>Name</Form.Label>
                <Form.Control type="name" placeholder="Enter Name" />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicAlter">
                <Form.Label>Alter</Form.Label>
                <Form.Control type="alter" placeholder="Alter" />
              </Form.Group>
              <Button variant="primary" type="submit" className="submit-button">
                Nutzer anlegen
              </Button>
            </Form>
            </Modal.Body>
          </Modal>

        </Nav>

      </Container>
    </>
    );
  }
}

export default App;
