import React, { Component } from 'react';
import { Button, Accordion } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import VrTrailApi from '../api/VrTrailApi';
import RunListEntry from './RunListEntry';

class UserDropdown extends Component {

    constructor(props) {
        super(props);

        let expandedID = null;

        if (this.props.location.expandRun) {
        expandedID = this.props.location.expandRun.getID();
        }

        // Init an empty state
        this.state = {
          run : [],
          open: false,
          currentUser: null,
          currentUserName: props.user,
          currentUserAge: null,
          expandedRunID: expandedID,
        };
    }

    handleOpen = () => {
        this.setState({
            open: true
        });
    }
    handleClose = () => {
        this.setState({
            open: false
        })
    }

    getRun = () => {
        VrTrailApi.getAPI().getRun()
        .then(runModels =>
            this.setState({
                run: runModels,
                error: null,
                loadingInProgress: false,
            }))
            .catch(e =>
                this.setState({
                    run: [],
                    error: e,
                    loadingInProgress: false,
                }));
        this.setState({
            error: null,
            loadingInProgress: true,
            loadingVorschlaegeError: null
        });
}

    getUserByName = () => {
        console.log(this.state.currentUserName)
		VrTrailApi.getAPI().getUserByName(this.state.currentUserName)
			.then(user =>
				this.setState({
                    currentUser: user,
                    currentUserAge: user.age,
					error: null,
					loadingInProgress: false,
				})).catch(e =>
					this.setState({
                        currentUser: null,
                        currentUserAge: null,
						error: e,
						loadingInProgress: false,
					}));
			this.setState({
				error: null,
				loadingInProgress: true
			});
	}

    showRuns() {
        console.log("He es geht")
    }

    componentDidMount() {
		this.getRun()
    }


    render() {
        const { run, expandedRunID }  = this.state;
        return (
        <div>
            { 
            run.map((item) => 
                <RunListEntry run={item} expandedState={expandedRunID === run.getID()}
                onExpandedStateChange={this.onExpandedStateChange}
                />
            )
            }
        </div>
        );
    }
  }
  export default UserDropdown;
