import '../App.css';
import { Button, Modal, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import React from 'react';
import RunList from './RunList';
import UserProfil from './UserProfil';
import '../style.css';
import VrTrailApi from '../api/VrTrailApi';


class Home extends React.Component {


  	/** Constructor of the app, which initializes firebase  */
	constructor(props) {
		super(props);

		// Init an empty state
		this.state = {
      run: [],

			show: false,
			showRunList: false,
			user: this.props.selectedUser,
      selectedUser: null
		};
	}

  handleUserChange(newUser) {
    this.setState({selectedUser: newUser});
    console.log("User_Right", newUser)
  }

  getUser() {
    this.setState({selectedUser: this.props.selectedUser});
    this.getRunByIdUser()
  }

  handleClose = () => {
    this.setState({
      show: false
    });
  }    
  handleShow = () => {
    this.setState({
      show: true
    });
  }

  getRunByIdUser = () => {
    console.log("")
    VrTrailApi.getAPI().getRunByIdUser(this.state.selectedUser.id)
    .then(runModels =>
        this.setState({
            run: runModels,
            error: null,
            loadingInProgress: false,
        }))
        .catch(e =>
            this.setState({
                run: [],
                error: e,
                loadingInProgress: false,
            }));
    this.setState({
        error: null,
        loadingInProgress: true,
        loadingRunError: null
    });
}

  componentDidMount() {
    this.getUser()
  }

  render() {
    return (
      this.props.currentUser ?
        <div className="d-flex flex-column mb-3">

          <div className="p-2">
              <UserProfil selectedUser={this.props.currentUser} runs={this.state.run}></UserProfil>
          </div>

          <div className="p-2">
              <RunList selectedUser={this.props.currentUser}></RunList>
          </div>

        </div>
      : null
    );
  }
}

export default Home;
