import React, { Component } from 'react';
import { Button, Card, Dropdown, DropdownButton } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import VrTrailApi from '../api/VrTrailApi';

class UserDropdown extends Component {

    constructor(props) {
        super(props);

        // Init an empty state
        this.state = {
            user: null,
            currentUser: null,
            urrentUserName: ' ',
            currentUserAge: null,
        };
    }

    // changeCurrentUser(newCurrentUser) {
    //     this.setState({
    //         currentUser: newCurrentUser
    //     });
    // }

    // getCurrentUser() {
    //     var user =  this.state.currentUser
    //     this.handleUserChange(user)
    // }

    // handleSelect(e){
    //     console.log("handleSelect", e)
    //     this.handleUserChange(e)
    // }

    handleUserChange(evt){
        this.props.handleUserChange(evt);
    }

    getUserByName = () => {
        console.log("he", this.props.selectedUser)
		VrTrailApi.getAPI().getUserByName(this.props.selectedUser)
			.then(user =>
				this.setState({
                    currentUser: user,
                    currentUserAge: user.age,
					error: null,
					loadingInProgress: false,
				})).catch(e =>
					this.setState({
                        currentUser: null,
                        currentUserAge: null,
						error: e,
						loadingInProgress: false,
					}));
			this.setState({
				error: null,
				loadingInProgress: true
			});
	};

    componentDidMount() {
        this.getUserByName()
    }


    render() {
        console.log("profil", this.state.currentUser)
        return (
        this.props.selectedUser ?
            <Card>
                <Card.Body>{this.state.currentUser.name}</Card.Body>
                <Card.Body>{this.props.selectedUser}</Card.Body>
            </Card>
        : null
        );
    }
  }
  export default UserDropdown;
