import '../App.css';
import { Button, Modal, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import React from 'react';
import RunList from './RunList';
import UserProfil from './UserProfil';
import '../style.css';
import VrTrailApi from '../api/VrTrailApi';


class Home extends React.Component {


  	/** Constructor of the app, which initializes firebase  */
	constructor(props) {
		super(props);

		// Init an empty state
		this.state = {
			show: false,
			showRunList: false,
			user: this.props.currentUser,
		};
	}

  getUserByName = () => {
    console.log(this.state.currentUserName)
    VrTrailApi.getAPI().getUserByName(this.state.currentUserName)
      .then(user =>
        this.setState({
                    currentUser: user,
                    currentUserAge: user.age,
          error: null,
          loadingInProgress: false,
        })).catch(e =>
          this.setState({
                        currentUser: null,
                        currentUserAge: null,
            error: e,
            loadingInProgress: false,
          }));
      this.setState({
        error: null,
        loadingInProgress: true
      });
  };

  render() {
    return (
        <div className="d-flex flex-column mb-3">

          <div className="p-2">
              <UserProfil selectedUser={this.props.currentUser}></UserProfil>
          </div>

          <div className="p-2">
            <Button id="run-button">Neuer Durchlauf starten</Button>
          </div>

          <div className="p-2">
              <RunList selectedUser={this.props.currentUser} runs={this.props.runs}></RunList>
          </div>

        </div>
    );
  }
}

export default Home;
