import React, { Component } from 'react';
import { Button, Accordion } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import VrTrailApi from '../api/VrTrailApi';

/**
 * Es wird ein einzelner Vorschlag für einen passenden Lernpartner oder /-gruppe mit allen not wendigen Informationen dargestellt
 */

class RunListEntry extends Component {
    constructor(props){
        super(props);

        // initiiere einen leeren state
        this.state = {
            run: props.run,
            currentPerson: props.currentPerson,

            //match: null,

            profil: null,
            gruppe: null,

            showRunDialog: false,

            loadingInProgress: false,
            error: null
        };
    }

    /** Handles onChange events of the underlying ExpansionPanel */
    expansionPanelStateChanged = () => {
    this.props.onExpandedStateChange(this.props.run);
    }

    //Handles the onClick event of the show profil button
    showProfilButtonClicked = (event) => {
      event.stopPropagation();
      this.setState({
        showRunDialog: true
      });
    }

    getRunByIdUser = () => {
        VrTrailApi.getAPI().getRunByIdUser(this.state.run.iduser)
        .then(runBO =>
            this.setState({
                run: runBO,
                error: null,
                loadingInProgress: false,
            })).catch(e =>
                this.setState({
                    run: null,
                    error: e,
                    loadingInProgress: false,
                }));
        this.setState({
            error: null,
            loadingInProgress: true,
            loadingKonversationenError: null
        });
      }

  /** Handles the onClose event of the CustomerForm */
  runDialogClosed = (profil) => {
    // customer is not null and therefor changed
    if (profil) {
      this.setState({

        showRunDialog: false
      });
    } else {
      this.setState({
        showRunDialog: false
      });
    }
  }

    componentDidMount() {
      // load Profil
      this.getRunByIdUser();
    }

    render(){
          const { classes, expandedState } = this.props;
          const { run } = this.state;

          return (
            <div>
                <Accordion>
                    <Accordion.Item eventKey={run.id - 1}>
                        <Accordion.Header>Durchlauf{run}</Accordion.Header>
                        <Accordion.Body>
                            Test
                        </Accordion.Body>
                    </Accordion.Item>
                    </Accordion>
            </div>
          );
        }
}

export default RunListEntry;
