import React, { Component } from 'react';
import { Accordion, Card } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import VrTrailApi from '../api/VrTrailApi';

class RunListEntry extends Component {
    constructor(props){
        super(props);

        // initiiere einen leeren state
        this.state = {
            run: props.run,
            runID: props.run.getID(),
            runDate: props.run.getDate(),

            profil: null,
            gruppe: null,

            showRunDialog: false,

            loadingInProgress: false,
            error: null
        };
    }

    /** Handles onChange events of the underlying ExpansionPanel */
    expansionPanelStateChanged = () => {
    this.props.onExpandedStateChange(this.props.run);
    }

    //Handles the onClick event of the show profil button
    showProfilButtonClicked = (event) => {
      event.stopPropagation();
      this.setState({
        showRunDialog: true
      });
    }

    getRunByIdUser = () => {
        VrTrailApi.getAPI().getRunByIdUser(this.state.run.iduser)
        .then(runBO =>
            this.setState({
                run: runBO,
                error: null,
                loadingInProgress: false,
            })).catch(e =>
                this.setState({
                    run: null,
                    error: e,
                    loadingInProgress: false,
                }));
        this.setState({
            error: null,
            loadingInProgress: true,
            loadingKonversationenError: null
        });
      }

  /** Handles the onClose event of the CustomerForm */
  runDialogClosed = (profil) => {
    // customer is not null and therefor changed
    if (profil) {
      this.setState({

        showRunDialog: false
      });
    } else {
      this.setState({
        showRunDialog: false
      });
    }
  }

    componentDidMount() {
      // load Profil
      this.getRunByIdUser();
    }

    render(){
          const { run, runID, runDate } = this.state;

          return (
            <div>
                <Accordion>
                  <Accordion.Item eventKey={runID - 1}>
                    <Accordion.Header>Durchlauf {runID}</Accordion.Header>
                    <Accordion.Body>
                          Datum: {runDate}
                          <br/><br/>
                          <Card.Body>
                            <Card.Subtitle>Task 1</Card.Subtitle>
                            <Card.Text>
                              Some quick example text to build on the card title and make up the
                              bulk of the card's content.
                            </Card.Text>
                          </Card.Body>
                          <br/>
                          <Card.Body>
                            <Card.Subtitle>Task 2</Card.Subtitle>
                            <Card.Text>
                              Some quick example text to build on the card title and make up the
                              bulk of the card's content.
                            </Card.Text>
                          </Card.Body>
                    </Accordion.Body>
                  </Accordion.Item>
                </Accordion>
            </div>
          );
        }
}

export default RunListEntry;
