.dotsWrapper {
  box-sizing: border-box;
}

.dotsWrapper *,
.dotsWrapper *:before,
.dotsWrapper *:after {
  box-sizing: inherit;
}

.dots {
  position: relative;
  width: 100%;
  padding-top: 6.6%;
}

.dots .dot {
  width: 6.6%;
  padding-top: 6.6%;
  animation: loader 2s ease-in-out infinite;
  border-radius: 100%;
  display: inline-block;
  position: absolute;
  top: 0;
  left: 0;
}

.dotsText {
  width: 100%;
  text-align: center;
}

@keyframes loader {
  0% {
    transform: translateX(0);
  }
  50% {
    transform: translateX(1420%);
  }
  100% {
    transform: translateX(0);
  }
}
import React from 'react';
import * as classes from './index.css';

export function Dots({
  dotColors = ['blue', 'violet', 'red', 'orange', 'yellow', 'green'],
  text = 'Loading...',
  id = '',
  className = '',
}) {
  // Setup newColors array
  const newColors = [];
  let dotColorsIndex = 0;
  let newColorsIndex = 0;
  while (newColorsIndex <= 5) {
    if (dotColors[dotColorsIndex]) {
      newColors.push(dotColors[dotColorsIndex]);
      newColorsIndex += 1;
      dotColorsIndex += 1;
    } else {
      dotColorsIndex = 0;
    }
  } // Setup array of dot elements

  let dots = new Array(6).fill('').map((e, index) => {
    return (
      <div
        key={index}
        className={classes.dot}
        style={{
          backgroundColor: newColors[index],
          animationDelay: `0.${index}s`,
        }}
      ></div>
    );
  });

  return (
    <div id={id} className={`${classes.dotsWrapper} ${className}`}>
      <div className={classes.dots}>{dots}</div>
      <div className={classes.dotsText}>{text}</div>
    </div>
  );
}
.minimalSpinnerWrapper {
  box-sizing: border-box;
}

.minimalSpinnerWrapper *,
.minimalSpinnerWrapper *:before,
.minimalSpinnerWrapper *:after {
  box-sizing: inherit;
}

.minimalSpinner {
  width: 100%;
  padding: 50% 0;
  border-radius: 100%;
  display: flex;
  align-items: center;
  border: 2px solid transparent;
  animation: rotate-spinner 1.5s linear 0s infinite forwards;
}

.minimalSpinnerText {
  text-align: center;
  position: absolute;
  width: 100%;
  animation: rotate-text 1.5s linear 0s infinite backwards;
}

@keyframes rotate-spinner {
  0% {
    transform: rotate(360deg);
  }
  100% {
    transform: rotate(0deg);
  }
}

@keyframes rotate-text {
  0% {
    transform: rotate(0deg);
  }
  100% {
    transform: rotate(360deg);
  }
}
import * as classes from './index.css';

export function MinimalSpinner({
  text = 'Loading...',
  color = 'blue',
  id = '',
  className = '',
}) {
  const style = `
    .${classes.minimalSpinner} {
      border-color: transparent ${color} transparent ${color};
    }
    .${classes.minimalSpinnerText} {
      color: ${color};
    }`;
  return (
    <div id={id} className={`${classes.minimalSpinnerWrapper} ${className}`}>
      <div className={classes.minimalSpinner}>
        <div className={classes.minimalSpinnerText}>{text}</div>
      </div>
      <style>{style}</style>
    </div>
  );
}
.progressBarWrapper {
  box-sizing: border-box;
}
.progressBarWrapper *,
.progressBarWrapper *:before,
.progressBarWrapper *:after {
  box-sizing: inherit;
}

.progressBar {
  width: 100%;
  padding: 5%;
  border-radius: 15px;
}

.sliderBackground {
  background-color: #000;
  border-radius: 15px;
}

.slider {
  padding-top: 5%;
  border-radius: 15px;
  animation: progres 4s infinite linear;
}
.progressBarText {
  width: 100%;
  text-align: center;
}
@keyframes progres {
  0% {
    width: 0%;
  }
  25% {
    width: 50%;
  }
  50% {
    width: 75%;
  }
  75% {
    width: 85%;
  }
  100% {
    width: 100%;
  }
} ;
import * as classes from './index.css';

export function ProgressBar({
  text = 'Loading...',
  borderColor = 'navy',
  sliderColor = 'white',
  sliderBackground = 'red',
  id = '',
  className = '',
}) {
  const style = `
    .${classes.progressBar} {
      background-color: ${borderColor};
    }
    .${classes.sliderBackground} {
      background-color: ${sliderBackground};
    }
    .${classes.slider} {
      background-color: ${sliderColor};
    }`;
  return (
    <div id={id} className={`${classes.progressBarWrapper} ${className}`}>
      <div className={classes.progressBar}>
        <div className={classes.sliderBackground}>
          <div className={classes.slider}></div>
        </div>
      </div>
      <div className={classes.progressBarText}>{text}</div>
      <style>{style}</style>
    </div>
  );
}
.spinnerWrapper {
  box-sizing: border-box;
}

.spinnerWrapper *,
.spinnerWrapper *:before,
.spinnerWrapper *:after {
  box-sizing: inherit;
  width: 100%;
}

.spinner {
  position: relative;
  display: flex;
  align-items: center;
  width: 100%;
}

.spinner .viewBox {
  animation: rotate 1s linear infinite;
  display: block;
}

.spinner .circle {
  stroke-linecap: round;
  animation: dash 2s ease-in-out infinite;
  position: absolute;
}

.spinner .spinnerText {
  position: absolute;
  width: 100%;
  text-align: center;
}

@keyframes rotate {
  100% {
    transform: rotate(360deg);
  }
}

@keyframes dash {
  0% {
    stroke-dasharray: 1, 150;
    stroke-dashoffset: 0;
  }
  50% {
    stroke-dasharray: 90, 150;
    stroke-dashoffset: -35;
  }
  100% {
    stroke-dasharray: 90, 150;
    stroke-dashoffset: -141;
  }
}
import * as classes from './index.css';

export function Spinner({
  id = '',
  className = '',
  color1 = 'green',
  color2 = 'lightGreen',
  text = 'Loading...',
  textColor = '',
  // noGradientText = false,
}) {
  let style = `.${classes.spinnerText} {
    background: linear-gradient(90deg, ${color1}, ${color2});
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    font-weight: bold;
  }`;

  if (textColor !== '') {
    style = `
      .${classes.spinnerText} {
      font-weight: bold;
      color: ${textColor}
      }`;
  }

  return (
    <div id={id} className={`${classes.spinnerWrapper} ${className}`}>
      <div className={classes.spinner}>
        <svg className={classes.viewBox} viewBox='0 0 50 50'>
          <circle
            className={classes.circle}
            cx='25'
            cy='25'
            r='22.5'
            fill='none'
            strokeWidth='5'
            stroke='url(#myGradient)'
          ></circle>
          <defs>
            <linearGradient id='myGradient'>
              <stop offset='0%' stopColor={color1} />
