import React, { Component } from 'react';
import { Button, Card, Dropdown, DropdownButton } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import VrTrailApi from '../api/VrTrailApi';

class UserDropdown extends Component {

    constructor(props) {
        super(props);

        // Init an empty state
        this.state = {
            user: null,
            currentUser: null,
            urrentUserName: ' ',
            currentUserAge: null,
        };
    }

    getUserByName = () => {
        console.log("he", this.props.currentUser)
		VrTrailApi.getAPI().getUserByName(this.props.currentUser)
			.then(user =>
				this.setState({
                    currentUser: user,
                    currentUserAge: user.age,
					error: null,
					loadingInProgress: false,
				})).catch(e =>
					this.setState({
                        currentUser: null,
                        currentUserAge: null,
						error: e,
						loadingInProgress: false,
					}));
			this.setState({
				error: null,
				loadingInProgress: true
			});
	};


    render() {
        return (
            this.props.currentUser ?
                <Card>
                    <Card.Body>{this.state.currentUser}</Card.Body>
                    <Card.Body>{this.props.currentUser.name}</Card.Body>
                </Card>
            : null
        );
    }
  }
  export default UserDropdown;
